import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Profilepage() {
  const [personprofile, setPersonProfile] = useState({});
  const [editableFields, setEditableFields] = useState({});
  const [editedFields, setEditedFields] = useState({});

  useEffect(() => {
    const authToken = localStorage.getItem('authToken');
    axios
      .get('http://127.0.0.1:8000/myapp/account', {
        headers: {
          Authorization: `${authToken}`,
        },
      })
      .then((res) => {
        setPersonProfile(res.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const handleEdit = (field) => {
    setEditableFields((prevFields) => ({ ...prevFields, [field]: true }));
  };

  const handleSave = (field) => {
    setEditableFields((prevFields) => ({ ...prevFields, [field]: false }));
    setEditedFields((prevFields) => ({ ...prevFields, [field]: personprofile[field] }));
  };

  const handleChange = (e, field) => {
    setEditedFields((prevFields) => ({ ...prevFields, [field]: e.target.value }));
  };

  const renderField = (field) => {
    if (editableFields[field]) {
      return (
        <div key={field}>
          <input
            type="text"
            value={editedFields[field] || personprofile[field]}
            onChange={(e) => handleChange(e, field)}
          />
          <button onClick={() => handleSave(field)}>Save</button>
        </div>
      );
    }
    return (
      <div key={field}>
        <span>{field}: {editedFields[field] || personprofile[field]}</span>
        <button onClick={() => handleEdit(field)}>Edit</button>
      </div>
    );
  };

  return (
    <div>
      <h1>{personprofile.name}</h1>
      {Object.keys(personprofile).map((field) => renderField(field))}
    </div>
  );
}

export default Profilepage;



