import { useLocation } from 'react-router-dom';
import React, { useEffect } from 'react';
import NavbarOption from './navbar';

const style = {
  BackGroundImageStyle: {
    background: 'linear-gradient(to bottom, #cddafd, #dfe7fd, #ffffff)',
    backgroundAttachment: 'fixed',
    top: '0',
    left: '0',
    position: 'fixed',
    height: '100%',
    width: '100%',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    zIndex: -1,
  },
  detailstyle: {
    position: 'absolute',
    top: '15%',
    left: '20%',
  },
  ModalBackground: {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.5)', // Semi-transparent background overlay
    zIndex: 9999,
  },
  
  modalContainer: {
    width: '45%',
    height: '75%',
    borderRadius: '12px',
    backgroundColor: 'rgba(255, 255, 255, 0.977)',
    boxShadow: '0px 5px 15px rgb(0, 0, 0)',
    display: 'flex',
    flexDirection: 'column',
    padding: '25px',
    // opacity: 1, // Change this to 1 since the modal is initially visible
    // animation: 'fadeIn 1.5s ease-in forwards',
  },
};


function UserDetailsContact({ closeModal }) {
  const location = useLocation();
  const user = location?.state?.user;

  useEffect(() => {
    console.log("here:", user);
  }, [user]);

  const handleSearch = (results) => {
    if (results.length === 0) {
      return;
    }
  }

  return (
    <div style={style.modalContainer}>
      <div style={style.BackGroundImageStyle}>
        <NavbarOption search={handleSearch}/>
        <div style={style.detailstyle}>
          {user ? (
            <React.Fragment>
              <h1>{user.name}</h1>
              <p>{user.email}</p>
              <p>{user.place}</p>
              <p>{user.prefix} - {user.phone}</p>
            </React.Fragment>
          ) : (
            <h1>User data not found.</h1>
          )}
        </div>
      </div>
      {/* Add a close button or element */}
      <button onClick={() => closeModal(false)}>Close</button>
    </div>
  );
}

export default UserDetailsContact;
