import React, { useEffect } from 'react';

function ItemDetails({ item }) {
  //     useEffect(() => {
  //   console.log("select:", item);
  // }, [item]);
  return (
    <div>
      {/* Access and display item properties */}
      <h2>Item Name: {item.item_name}</h2>
      <p>Animal: {item.animal}</p>
      <p>Size: {item.size}</p>
      <p>Description: {item.description}</p>
      <p>Image: <img src={`http://127.0.0.1:8000${item.image}`} alt="img" className="small-image" /></p>
    </div>
  );
}

export default ItemDetails;
