import { useState } from 'react';
import React from 'react';
// import back from '../photos/back.jpg';
import back from '../photos/back5.png';
import Loginpage from './loginpage';
import Signup from './signup';
import './homepage.css';
import { useNavigate } from 'react-router-dom';

const styles = {
    BackGroundImage:{
    backgroundImage: `url(${back})`,
    position: 'absulut',
    height: '100%',
    width: '100%',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    position: 'absolute',
  },
  LoginButtom:{
    position: 'absulut',
    top: '40%', 
    left: '20%',
  },
};


function Homepage() {
  const [openModal, setOpenModal] = useState(false);
  const [openSignUpModal, setopenSignUpModal] = useState(false);

  const navigate = useNavigate();
  const handleLoginButtonClick = () => {
    setOpenModal(true);
    setopenSignUpModal(false);
  }

  const handleSignupButtonClick = () => {
    setOpenModal(false);
    setopenSignUpModal(true);
  }
 
  return (
    <div style={styles.BackGroundImage}>
      <h1 className='display-1 position-absolute top-0 start-50 end -0 translate-middle-x'>AniHelp</h1>
          <div>
            <button className="openModalBtn" onClick={handleLoginButtonClick}>login</button>
            {openModal && <Loginpage closeModal= {setOpenModal}/>}  
          </div>
          <div>
            <button className="openSignUpModal" onClick={handleSignupButtonClick}>Sign up</button>
            {openSignUpModal && <Signup closeSignUpModal= {setopenSignUpModal}/>} 
          </div>
          <div>
           <button className="AboutBtn" onClick={() => navigate('/about')}>About</button>
          </div>
    </div>
  );
}

export default Homepage;
         

