import React from 'react';
import NavbarOption from './navbar';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import "./userportal.css"; 
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import axios from 'axios';
import { useEffect, useState } from 'react';

const style = {
  width: '100%',
  maxWidth: 1250,
  bgcolor: 'background.paper',
};





function Userportalpage() {
  const[items, setItems] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All'); 
  useEffect(() => {
    axios
      .get('http://127.0.0.1:8000/myapp/item')
      .then((res) => {
        console.log(res.data);
        console.log(res.data.id);
        setItems(res.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);


  useEffect(() => {
    console.log("item:", items);
  }, [items]);

  return (
    <div className="user-portal-page">
      <NavbarOption />
      <div className="button-container">
        <List sx={style} component="nav" aria-label="mailbox folders">
      <ListItem button>
        <ListItemText primary="All" />
      </ListItem>
      <ListItem button>
        <ListItemText primary="Toys" />
      </ListItem>
      <ListItem button>
        <ListItemText primary="Food & Related" />
      </ListItem>
      <ListItem button>
        <ListItemText primary="Sleep" />
      </ListItem>
      <ListItem button>
        <ListItemText primary="Clothing" />
      </ListItem>
      <ListItem button>
        <ListItemText primary="Strap" />
      </ListItem>
      <ListItem button>
        <ListItemText primary="Cages" />
      </ListItem>
    </List>
    <div className="button-container">
        <List sx={style} component="nav" aria-label="mailbox folders">
          <ListItem button onClick={() => setSelectedCategory('All')}> {/* Update the selected category */}
            <ListItemText primary="All" />
          </ListItem>
          {/* ... (other categories) */}
        </List>
      </div>
      <div className="items-container">
        {items
          .filter(item => selectedCategory === 'All' || item.category === selectedCategory) // Filter items based on the selected category
          .map(item => (
            <div key={item.id}>
              {/* Display item information here */}
              <p>{item.name}</p>
              {/* ... (other item information) */}
            </div>
          ))}
      </div>
    </div>
    </div>
  );
}

export default Userportalpage;




