import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Profilepage() {
  const [personprofile, setPersonProfile] = useState({});
  const [editableFields, setEditableFields] = useState({});

  useEffect(() => {
    const authToken = localStorage.getItem('authToken');
    axios
      .get('http://127.0.0.1:8000/myapp/account', {
        headers: {
          Authorization: `${authToken}`,
        },
      })
      .then((res) => {
        setPersonProfile(res.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const handleEdit = (field) => {
    setEditableFields((prevFields) => ({ ...prevFields, [field]: true }));
  };

  const handleChange = (e, field) => {
    setPersonProfile((prevProfile) => ({ ...prevProfile, [field]: e.target.value }));
  };

  const handleSave = (field) => {
    setEditableFields((prevFields) => ({ ...prevFields, [field]: false }));
    // Perform API call to save the updated field
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h1>{personprofile.name}</h1>
      <p>
        Name: {editableFields.name ? (
          <input type="text" value={personprofile.name} onChange={(e) => handleChange(e, 'name')} />
        ) : (
          <>
            {personprofile.name}
            <button onClick={() => handleEdit('name')}>Edit</button>
          </>
        )}
      </p>
      <p>
        Gender: {editableFields.gender ? (
          <input type="text" value={personprofile.gender} onChange={(e) => handleChange(e, 'gender')} />
        ) : (
          <>
            {personprofile.gender}
            <button onClick={() => handleEdit('gender')}>Edit</button>
          </>
        )}
      </p>
      <p>
        Age: {editableFields.age ? (
          <input type="text" value={personprofile.age} onChange={(e) => handleChange(e, 'age')} />
        ) : (
          <>
            {personprofile.age}
            <button onClick={() => handleEdit('age')}>Edit</button>
          </>
        )}
      </p>
      <p>
        Residence: {editableFields.place ? (
          <input type="text" value={personprofile.place} onChange={(e) => handleChange(e, 'place')} />
        ) : (
          <>
            {personprofile.place}
            <button onClick={() => handleEdit('place')}>Edit</button>
          </>
        )}
      </p>
      <p>
        Phone number: {editableFields.phone ? (
          <input type="text" value={personprofile.phone} onChange={(e) => handleChange(e, 'phone')} />
        ) : (
          <>
            {personprofile.phone}
            <button onClick={() => handleEdit('phone')}>Edit</button>
          </>
        )}
      </p>
      <button onClick={() => handleSave()}>Save All</button>
    </div>
  );
}

export default Profilepage;
