import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Profilepage() {
  const [personprofile, setPersonProfile] = useState({});

  useEffect(() => {
    const authToken = localStorage.getItem('authToken');
    axios
      .get('http://127.0.0.1:8000/myapp/account', {
        headers: {
          Authorization: `${authToken}`,
        },
      })
      .then((res) => {
        setPersonProfile(res.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  return (
    <div
      style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        textAlign: 'center',
      }}
    >
      <h1 style={{ margin: '-50' }}>{personprofile.name}</h1>
      <p style={{ margin: '0' }}>Name: {personprofile.name}</p>
      <p style={{ margin: '0' }}>Gender: {personprofile.gender}</p>
      <p style={{ margin: '0' }}>Age: {personprofile.age}</p>
      <p style={{ margin: '0' }}>Residence: {personprofile.place}</p>
      <p style={{ margin: '0' }}>Phone number: {personprofile.prefix}{personprofile.phone}</p>
    </div>
  );
}

export default Profilepage;
