import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import "./loginpage.css";









// const Loginpage = () => {
//     const handleClick = () => {
//       window.open('/popup-page', 'Popup', 'width=600,height=400');
//     };
  
//     return (
//       <button onClick={handleClick}>Open Popup</button>
//     );
//   };
  
//   export default Loginpage;



function Loginpage({closeModal}) {
  const [data, setData] = useState([]);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [userDetails, setUserDetails] = useState(null);

  let navigate = useNavigate();

  useEffect(() => {
    setUserDetails({ name, email, phone });
  }, [name, email, phone]);

  const handleNameChange = (event) => {
    setName(event.target.value);
  }

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  }

  const handlePhoneChange = (event) => {
    setPhone(event.target.value);
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    axios.post('/api/user', userDetails)
      .then(response => {
        console.log(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }

  const handleChange = (e, myKey) =>{
    setData(prevdata=>({...prevdata, [myKey]:e.target.value}))    
  }
  useEffect(()=>{console.log(data)} ,[data])
    
  
  return (
    <div className='ModalBackground'>
      <div className='modalContainer'>
      <div className='title'>
        <h1>Login</h1>
      </div>
        <form onSubmit={handleSubmit}>
        <label>
              <input type="text" placeholder="Enter your user name" value={data.username} onChange={(e)=>{handleChange(e,'username')}}  />
            </label>
            <br />
            <label>
              <input type="email" placeholder="Enter your email" value={data.email} onChange={(e)=>{handleChange(e,'email')}} />
            </label>
          <button className='submitBtn' type="submit">Submit</button>
        </form>
          <div className='cancelBtn'>
           <button onClick={()=>closeModal(false)}>cancel</button>
          {/* <button onClick={()=>navigate("/")}>Back Home</button> */}
          </div>
      </div>
    </div>
  );
}

export default Loginpage;

  



