import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import NavbarOption from './navbar';

function ItemDetails() {
  const location = useLocation();
  const item = location?.state?.item;

  useEffect(() => {
    console.log('select:', item);
  }, [item]);

  return( 
      <div>
        <NavbarOption />
        <div>
        {/* Use the img tag with the src attribute set to the image URL */}
        {item && (
          <img
            src={`http://127.0.0.1:8000${item.image}`}
            alt={item.item_name} // Add an alt attribute for accessibility
            style={{ width: '200px', height: '150px',position: 'absolute', top: '45%', left: '10%' }}
          />
        )}
      </div>
      <h1>{item.item_name}</h1>;
      <p>{item.description}</p>
    </div>
  );
}

export default ItemDetails;
