import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import NavbarOption from './navbar';
import { useNavigate } from 'react-router-dom';

import {
  EmailShareButton,
  FacebookShareButton,
  HatenaShareButton,
  InstapaperShareButton,
  LineShareButton,
  LinkedinShareButton,
  LivejournalShareButton,
  MailruShareButton,
  OKShareButton,
  PinterestShareButton,
  PocketShareButton,
  RedditShareButton,
  TelegramShareButton,
  TumblrShareButton,
  TwitterShareButton,
  ViberShareButton,
  VKShareButton,
  WhatsappShareButton,
  WorkplaceShareButton
} from "react-share";


const styles = {
  backButton: {
    position: 'absolute',
    top: '12%',
    left: '3%',
    padding: '0.8%',
    backgroundColor: 'white',
    border: 'none',
    cursor: 'pointer',
  },
  imageDiv: {
    position: 'absolute', 
    top: '20%', 
    left: '20%',
  },
  image:{
    width: '25%', 
    height: '40%',
  },
  style_name:{
    position: 'absolute', 
    top: '40%', 
    left: '20%',
  },
  item_details:{
    position: 'absolute', 
    top: '50%', 
    left: '20%',
  },
  shars:{
    position: 'absolute', 
    top: '80%', 
    left: '20%',
  },
};


function ItemDetails() {
  const location = useLocation();
  const item = location?.state?.item;
  const navigate = useNavigate();
  
  const urlToShare = "https://www.example.com"; 

  return( 
      <div>
        <NavbarOption />
        <button style={styles.backButton} onClick={() => navigate('/')}>
          Back
        </button>
        <div style={styles.imageDiv}>
        {item && (
          <img
            src={`http://127.0.0.1:8000${item.image}`}
            alt={item.item_name}
            style={styles.image}
          />
        )}
        </div>
      <h1 style={styles.style_name}>{item.item_name}</h1>;
      <div style={styles.item_details}>
        <p>adapted to: {item.animal}</p>
        <p>size: {item.size}</p>
        <p>description: {item.description}</p>
      </div>
      <div>
        <FacebookShareButton url={urlToShare}>
          <img src="/path/to/facebook-logo.png" alt="Share on Facebook" />
        </FacebookShareButton>
    </div>
    </div>
  );
}

export default ItemDetails;
