import { useState } from 'react';
import React from 'react';
// import back from '../photos/back.jpg';
import back from '../photos/back5.png';
import Loginpage from './loginpage';
import Signup from './signup';
import './homepage.css';
import { useNavigate } from 'react-router-dom';

const styles = {
    BackGroundImage:{
    backgroundImage: `url(${back})`,
    position: 'absulut',
    height: '100%',
    width: '100%',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    position: 'absolute',
  },
  signupModalBtn:{
    position: 'absolute',
    top: '30%',
    left: '10%', 
  },
  signupPop:{
    position: 'absolute',
    top: '4%',
    left: '21%', 
  },
};


function Homepage() {
  const [openModal, setOpenModal] = useState(false);
  const [openSignUpModal, setopenSignUpModal] = useState(false);

  const navigate = useNavigate();
  const handleLoginButtonClick = () => {
    setOpenModal(true);
    setopenSignUpModal(false);
  }

  const handleSignupButtonClick = () => {
    setOpenModal(false);
    setopenSignUpModal(true);
  }
 
  return (
    <div style={styles.BackGroundImage}>
      <h1>AniHelp</h1>
        <nav className='bd-highlight" w-20'>
          <div>
          <button className="loginModalBtn" onClick={handleLoginButtonClick}>login</button>
          {openModal && <Loginpage closeModal= {setOpenModal}/>}  
            {/* <Link to="/Login">Login</Link> */}
          </div>
        </nav>
        <nav >
          <div style={styles.signupPop}>
          <button className="openSignUpModal" onClick={handleSignupButtonClick} style={styles.signupModalBtn}>Sign up</button>
          {openSignUpModal && <Signup closeSignUpModal= {setopenSignUpModal}/>} 
            {/* <Link to="signup">Sign up</Link> */}
          </div>
        </nav>
        
        <div>
           <button className="AboutBtn" onClick={() => navigate('/about')}>About</button>
          </div>
        <nav>
      </nav>
    </div>
  );
}

export default Homepage;
