import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import NavbarOption from './navbar';
import { useNavigate } from 'react-router-dom';


const styles = {
  backButton: {
    position: 'absolute',
    top: '12%',
    left: '3%',
    padding: '10px',
    backgroundColor: '#eaeaea',
    border: 'none',
    cursor: 'pointer',
  },
  imageDiv: {
    position: 'absolute', 
    top: '20%', 
    left: '20%',
  },
  image:{
    width: '25%', 
    height: '40%',
  },
};


function ItemDetails() {
  const location = useLocation();
  const item = location?.state?.item;
  const navigate = useNavigate();
  

  return( 
      <div>
        <NavbarOption />
        <button style={styles.backButton} onClick={() => navigate('/')}>
          Back
        </button>
        <div style={styles.imageDiv}>
        {item && (
          <img
            src={`http://127.0.0.1:8000${item.image}`}
            alt={item.item_name} // Add an alt attribute for accessibility
            style={styles.image}
          />
        )}
        </div>
      <h1 style={{position: 'absolute', top: '40%', left: '20%' }}>{item.item_name}</h1>;
      <div style={{position: 'absolute', top: '50%', left: '20%' }}>
        <p>adapted to: {item.animal}</p>
        <p>size: {item.size}</p>
        <p>description: {item.description}</p>
      </div>
    </div>
  );
}

export default ItemDetails;
