import { useState } from 'react';
import React from 'react';
// import back from '../photos/back.jpg';
import back from '../photos/back5.png';
import Loginpage from './loginpage';
import Signup from './signup';
import './homepage.css';
import { useNavigate } from 'react-router-dom';

const styles = {
    BackGroundImage:{
    backgroundImage: `url(${back})`,
    position: 'absulut',
    height: '100%',
    width: '100%',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    position: 'absolute',
  },
  loginNav: {
    position: 'absolute',
    top: '70%',  // Adjust the top position to move the login button vertically
    left: '30%', // Adjust the left position to move the login button horizontally
    transform: 'translate(-50%, -50%)', // Center the login button in the middle
  },
  signupNav: {
    position: 'absolute',
    top: '70%', // Adjust the top position to move the signup button vertically
    left: '21%', // Adjust the left position to move the signup button horizontally
    transform: 'translate(-50%, -50%)',
  },
};


function Homepage() {
  const [openModal, setOpenModal] = useState(false);
  const [openSignUpModal, setopenSignUpModal] = useState(false);

  const navigate = useNavigate();
  const handleLoginButtonClick = () => {
    setOpenModal(true);
    setopenSignUpModal(false);
  }

  const handleSignupButtonClick = () => {
    setOpenModal(false);
    setopenSignUpModal(true);
  }
 
  return (
    <div style={styles.BackGroundImage}>
      <h1 className='display-1 position-absolute top-0 start-50 translate-middle-x'>AniHelp</h1>
        <nav className='bd-highlight" w-20'>
          <div style={styles.loginNav}>
          <button className="openModalBtn" onClick={handleLoginButtonClick}>login</button>
          {openModal && <Loginpage closeModal= {setOpenModal}/>}  
            {/* <Link to="/Login">Login</Link> */}
          </div>
        </nav>
        <nav >
          <div style={styles.loginNav}>
          <button className="openSignUpModal" onClick={handleSignupButtonClick}>Sign up</button>
          {openSignUpModal && <Signup closeSignUpModal= {setopenSignUpModal}/>} 
            {/* <Link to="signup">Sign up</Link> */}
          </div>
        </nav>
        <nav className='position-absolute top-0 end-0 w-20'>
        <div>
           <button className="AboutBtn" onClick={() => navigate('/about')}>About</button>
          </div>
        </nav>
        <nav>
      </nav>
    </div>
  );
}

export default Homepage;
