import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import NavbarOption from './navbar';

function ItemDetails() {
  const location = useLocation();
  const item = location?.state?.item;

  useEffect(() => {
    console.log('select:', item);
  }, [item]);

  return( 
      <div>
        <NavbarOption />
        <div style={{ position: 'absolute', top: '20%', left: '20%' }}>
        {item && (
          <img
            src={`http://127.0.0.1:8000${item.image}`}
            alt={item.item_name} // Add an alt attribute for accessibility
            style={{ width: '25%', height: '40%'}}
          />
        )}
        </div>
      <h1 style={{position: 'absolute', top: '40%', left: '20%' }}>{item.item_name}</h1>;
      <div style={{position: 'absolute', top: '50%', left: '20%' }}>
        <p>adapted to: {item.animal}</p>
        <p>size: {item.size}</p>
        <p>description: {item.description}</p>
      </div>
    </div>
  );
}

export default ItemDetails;
