import React, { useState, useEffect } from 'react';
import NavbarOption from './navbar';
import axios from 'axios';
import {
  Button,
  Stack,
  List,
  ListItem,
  ListItemText,
  Card,
  CardHeader,
  CardMedia,
  CardContent,
  CardActions,
  Collapse,
  Avatar,
  IconButton,
  Typography,
} from '@mui/material';
import { red } from '@mui/material/colors';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import MoreVertIcon from '@mui/icons-material/MoreVert';

function UserPortalPage() {
  const [items, setItems] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [isShareScriptLoaded, setShareScriptLoaded] = useState(false);

  useEffect(() => {
    // Load the ShareThis script when the component mounts
    const script = document.createElement('script');
    script.src =
      'https://platform-api.sharethis.com/js/sharethis.js#property=64bd31f41734a80012bd605f&product=inline-share-buttons&source=platform';
    script.async = true;
    script.onload = () => {
      setShareScriptLoaded(true);
    };
    document.body.appendChild(script);

    // Clean up the script tag when the component unmounts
    return () => {
      document.body.removeChild(script);
    };
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const authToken = localStorage.getItem('authToken');
        const response = await axios.get('http://127.0.0.1:8000/myapp/menu', {
          params: { selectedCategory },
        });
        console.log("menu items:", response.data);
        setItems(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, [selectedCategory]); // useEffect will be triggered whenever selectedCategory changes

  const handleSelect = (newSelect) => {
    setSelectedCategory(newSelect);
  };

  const handleExpandClick = (itemId) => {
    setItems((prevItems) =>
      prevItems.map((item) => {
        if (item.id === itemId) {
          return { ...item, expanded: !item.expanded };
        }
        return item;
      })
    );
  };

  function handleShare(item) {
    if (isShareScriptLoaded && navigator.share) {
      // Share using native share
      navigator
        .share({
          title: item.item_name,
          text: `Check out this item intended for ${item.animal} size ${item.size}`,
          url: window.location.href,
        })
        .then(() => console.log('Shared successfully.'))
        .catch((error) => console.error('Error sharing:', error));
    } else {
      console.log('Native sharing not supported.');
      // Handle fallback sharing here if needed
    }
  }

  return (
    <div className="user-portal-page">
      <NavbarOption />
      <div className="button-container">
        <List sx={{ width: '100%', maxWidth: 1250, bgcolor: 'background.paper' }} component="nav" aria-label="mailbox folders">
          <ListItem button onClick={() => handleSelect('All')}>
            <ListItemText primary="All" />
          </ListItem>
          <ListItem button onClick={() => handleSelect('toys')}>
            <ListItemText primary="Toys" />
          </ListItem>
          <ListItem button onClick={() => handleSelect('food and related products')}>
            <ListItemText primary="Food & Related" />
          </ListItem>
          <ListItem button onClick={() => handleSelect('sleep')}>
            <ListItemText primary="Sleep" />
          </ListItem>
          <ListItem button onClick={() => handleSelect('clothing')}>
            <ListItemText primary="Clothing" />
          </ListItem>
          <ListItem button onClick={() => handleSelect('straps')}>
            <ListItemText primary="Straps" />
          </ListItem>
          <ListItem button onClick={() => handleSelect('cages')}>
            <ListItemText primary="Cages" />
          </ListItem>
        </List>
      </div>
      <div className="items-container" style={{ width: '100%', maxWidth: 1250, margin: '0 auto', bgcolor: 'background.paper', display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '16px' }}>
        {items.map((item) => (
          <Card key={item.id} sx={{ maxWidth: 345 }}>
            <CardHeader
              avatar={<Avatar sx={{ bgcolor: red[500] }} aria-label="recipe">R</Avatar>}
              action={<IconButton aria-label="settings"><MoreVertIcon /></IconButton>}
              title={item.item_name}
              subheader="September 14, 2016"
            />
            <CardMedia component="img" height="194" image={`http://127.0.0.1:8000${item.image}`} alt="Paella dish" />
            <CardContent>
              <Typography variant="body2" color="text.secondary">
                Intended for: {item.animal} size: {item.size}
              </Typography>
            </CardContent>
            <CardActions disableSpacing id={`card-actions-${item.id}`}>
              <IconButton aria-label="add to favorites">
                <FavoriteIcon />
              </IconButton>
              <IconButton aria-label="share" onClick={() => handleShare(item)}>
                <ShareIcon />
              </IconButton>
              <ExpandMore
                expand={item.expanded}
                onClick={() => handleExpandClick(item.id)}
                aria-expanded={item.expanded}
                aria-label="show more"
              >
                <ExpandMoreIcon />
              </ExpandMore>
            </CardActions>
            <Collapse in={item.expanded} timeout="auto" unmountOnExit>
              <CardContent>
                <Typography paragraph>Description:</Typography>
                <Typography paragraph>{item.description}</Typography>
              </CardContent>
            </Collapse>
          </Card>
        ))}
      </div>
    </div>
  );
}

export default UserPortalPage;




